# 9. Quelle est la position dans l'array de la personne @epenser ?
def position_element
        position = $journalistes.index { |x| x == '@epenser' }
    puts "9. la position dans l'array de la personne @epenser est #{position}"
    puts "-" * 40
end


# fonction qui va appeler toutes les autres fonctions pour les afficher
def perfom
    sort_array
    arrayJournalistes
    count_Integer
    how_many_aude
    count_maj_per_letter
    count_maj
    find_underscore
    position_element
end
perfom
